CREATE TABLE raid_type (
  id BIGINT PRIMARY KEY,
  name VARCHAR,
  message VARCHAR,
  area VARCHAR,
  subarea VARCHAR,
  window_min INT,
  window_max INT,
  event_start DATE,
  event_end DATE
);
CREATE TABLE raid (
  raid_id UUID PRIMARY KEY,
  raid_type_id BIGINT,
  area VARCHAR,
  subarea VARCHAR,
  start_date TIMESTAMPTZ
);

